document.addEventListener("DOMContentLoaded",function(){const t=document.createElement("button");t.className="scroll-to-top",t.innerHTML='<svg width="24" height="24" viewBox="0 0 24 24" fill="none" stroke="currentColor" stroke-width="2" stroke-linecap="round" stroke-linejoin="round"><path d="M12 19V5M5 12l7-7 7 7"/></svg>',t.setAttribute("aria-label","Scroll naar boven"),document.body.appendChild(t);function o(){window.scrollY>300?t.classList.add("visible"):t.classList.remove("visible")}function n(){window.scrollTo({top:0,behavior:"smooth"})}window.addEventListener("scroll",o),t.addEventListener("click",n)});
